import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {addList, deleteList } from './counterSlice'

const Etracker = () => {
  const ar = useSelector((state) => state.counter.arr);
  const dispatch = useDispatch();

  const [details, setDetails] = useState({
    pname : '',
    amount : ''
  });

  const Handler = (e) =>{
    const {name, value} = e.target;
    setDetails((vl) =>{
      return {
        ...vl,
        [name] : value
      }
    });
  }

  const submit = () =>{
    const newItem = {
      id: Date.now(),  // Unique ID
      pname: details.pname,
      amt: details.amount,
    };
    dispatch(addList(newItem));
    setDetails({ pname: '', amount: '' });  // Reset input fields
  }

  const edit = (vl) =>{
    dispatch(editList(vl));
    window.prompt("Product Name", vl.id);
  }

  console.log(ar);

  const dlt = (id) =>{
    dispatch(deleteList(id));
  }

  const balAmt = 1500;
  const totalAmt = ar.reduce((accum, cvalue) =>accum + parseInt(cvalue.amt), 0);

  return (
    <>
      <div className='etracker_main'>
        <div className='your_balance'>
          <p>Your Balance</p>
          <h4>Rs.{balAmt}</h4>
        </div>

        <div className='income_expense'>
          <div className='income_expense_left'>
            <h4>Balance</h4>
            <p>Rs.{balAmt-totalAmt}</p>
          </div>
          <div className='income_expense_right'>
            <h4>Expense</h4>
            <p>Rs. {totalAmt}</p>
          </div>
        </div>

        <div className='etrack_exp_list'>
          <p>Expence List</p>
          {
            ar.map((v, i) =>{
              return(
                <>
                <ul>
                    <li>{v.pname}</li>
                    <li>{v.amt}
                    <i className='fas fa-edit tr_delete' onClick={() => edit(v)}></i>
                    <i className='fas fa-trash-alt tr_delete' onClick={() => dlt(v.id)}></i>
                    </li>
                </ul>
                </>
              )
            })
          }
        </div>

        <div className='add_new_transaction'>
          <form>
            <p>Product Name</p>
            <input type='text' name='pname' value={details.pname} onChange={Handler} />
            <p>Amount</p>
            <input type='number' name='amount' value={details.amount} onChange={Handler} />
            <div className='etrack_btn'><button type='button' name='btn' onClick={submit}>Add</button></div>
          </form>
        </div>
      </div>
    </>
  )
}

export default Etracker;
