import {useState} from 'react';
import {apiData, fetchApiData} from './reducer'
import { useSelector, useDispatch } from 'react-redux';

const Api = () =>{
    const count = useSelector(state => state.counterq.val);
    const dispatch = useDispatch();
    const [num, setNum] = useState();

    //dispatch(apiData(num));
    //dispatch(fetchApiData(num));
    useEffect(() => {
        if (num) {
            dispatch(fetchApiData(num));
        }
    }, [num, dispatch]); // Fetch API data only when `num` changes

    const handler = (e) =>{
        setNum(e.target.value);
    }

    return(
        <>
            {count}
            <pre>{JSON.stringify(count, null, 2)}</pre>
            <select className="form-select" style={{width:'200px'}} onChange={handler}>
                <option value=''>select</option>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
            </select>
        </>
    )
}

export default Api;
