import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { add } from './reducer'

function Counter() {
  const count = useSelector(state => state.counterq.val);
  const dispatch = useDispatch();
  const [data, setdata] = useState({
    name: ''
  });

  const handler = (e) =>{
    const {name, value} = e.target;
    setdata((oldval) =>{
      return{
        ...oldval,
        [name]: value
      }
    })
  }

  const submit = () =>{
    if(data.name.trim() !== ''){
    dispatch(add(data.name));
    data.name = "";
  }else{
    alert('Please enter name');
  }
  }

  //console.log(count);

  return (
    <div>
      <div>
        <input type='text' name='name' value={data.name} onChange={handler}/>
        <button onClick={submit}>Add</button>
        <ul className='test_list'>
          {
            count.map((val, ind) =>{
              console.log(val.value);
              console.log(val.id);
              {/* return(
                <>
                  <li key={ind}>{val}<span className='test_btn'>Edit</span></li>
                </>
              ) */}
            })
          }
        </ul>
      </div>
    </div>
  )
}

export default Counter;
