import { createSlice } from '@reduxjs/toolkit'

const initialState = {
 val: 1,
}

export const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    // testing: (state) =>{
    //   const ran = Math.floor(Math.random() * state.clr.length);
    //   state.selectedValue = state.clr[ran];
    //   document.body.style.backgroundColor = state.selectedValue;
    // },
    faq: (state, action) =>{
      state.val +=  action.payload
    },
  },
})

// Action creators are generated for each case reducer function
export const { faq } = counterSlice.actions

export default counterSlice.reducer
