import { useEffect, useState } from 'react';
import QuizData from './QuizData';

const Faq = () =>{
    const [marks, setmarks] = useState(0);
    const [slv, setslv] = useState();
    const [num, numData] = useState(0);
    const [sr, setsr] = useState(1);
    const vl = QuizData[num];
    const lt = QuizData.length-1;

    const next = () =>{
        numData(num + 1);
        if(num === lt){
            numData(0);
            alert('completed');
        }
        setsr(sr + 1);
        if(sr === QuizData.length){
            setsr(1);
        }
        //console.log(slv);
        const rd = document.querySelectorAll('#radio1');
        rd.forEach((n) =>{
            n.checked = false;
        })

        useEffect(() =>{
            if(slv === vl.ans){
                console.log('pass');
                setmarks(marks + 1);
            }else{
                console.log('failled');
            }
        })
    }

    const Handler = (e) =>{
        setslv(e.target.value);
    }

    //console.log(slv);
    //console.log(vl.ans);

    return(
        <>
            <div className="quiz">
                <h4>{sr}. {vl.qs}</h4>

                <div className="rbtn">
                <input type="radio" name="ans" value={vl.a} id='radio1' onChange={Handler}/><label>{vl.a}</label>
                </div>
                <div className="rbtn">
                <input type="radio" name="ans" value={vl.b} id='radio1' onChange={Handler}/><label>{vl.b}</label>
                </div>
                <div className="rbtn">
                <input type="radio" name="ans" value={vl.c} id='radio1' onChange={Handler}/><label>{vl.c}</label>
                </div>
                <div className="rbtn">
                <input type="radio" name="ans" value={vl.d} id='radio1' onChange={Handler}/><label>{vl.d}</label>
                </div>
                <button type="button" onClick={next}>Next</button>
                <p>Marks : {marks}</p>
            </div>
        </>
    )
}

export default Faq;
