import { createSlice } from '@reduxjs/toolkit';
import { nanoid } from 'nanoid';

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    val: []
  },
  reducers: {
    add: (state, actions) => {
      //state.val.push(actions.payload);
      state.val.push({id: nanoid(), value: actions.payload});
    },
    crt: (state, actions) =>{
      //console.log(actions.payload);
      actions.payload.filter((id) =>{
        console.log(id);
      });
    }
  }
});

// Action creators are generated for each case reducer function
export const { add, crt} = counterSlice.actions

export default counterSlice.reducer
