import { useState, useEffect } from 'react';
import { fetchApiData } from './reducer';
import { useSelector, useDispatch } from 'react-redux';

const Api = () => {
    const dispatch = useDispatch();
    const apiResult = useSelector(state => state.counter.apiResult);
    const status = useSelector(state => state.counter.status);
    const error = useSelector(state => state.counter.error);
    const [num, setNum] = useState('');

    useEffect(() => {
        if (num) {
            dispatch(fetchApiData(num));
        }
    }, [num, dispatch]);

    const handler = (e) => {
        setNum(e.target.value);
    };

    return (
        <>
            <select className="form-select" style={{ width: '200px' }} onChange={handler}>
                <option value="">select</option>
                <option value="1">1</option>
                <option value="2">2</option>
                <option value="3">3</option>
                <option value="4">4</option>
                <option value="5">5</option>
            </select>

            <div style={{ marginTop: '20px' }}>
                {status === 'loading' && <p>Loading...</p>}
                {status === 'failed' && <p style={{ color: 'red' }}>Error: {error}</p>}
                {status === 'succeeded' && apiResult && (
                    <div>
                        <h3>{apiResult.title}</h3>
                        <p>{apiResult.body}</p>
                    </div>
                )}
            </div>
        </>
    );
};

export default Api;
