import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {list, deleteItem, editItem} from './counterSlice'

export function Counter() {
  const [val, setval] = useState({
    name: ''
  });
  const pd = {
    padding: '2px',
    backgroundColor: '#ccc',
    borderRadius: '10px',
    cursor: 'pointer'
  }
  const count = useSelector((state) => state.counter.val)
  const dispatch = useDispatch();

  const handler = (e) =>{
    const {name, value} = e.target;
    setval((oldval) =>{
      return{
        ...oldval,
        [name]: value
      }
    })
  }

  const btn = () =>{
    dispatch(list(val.name));
  }

  const dltcl = (id) =>{
    dispatch(deleteItem(id));
  }

  const editList = (id) =>{
    dispatch(editItem(id));
  }

  return (
    <>
    <input type='text' name='name' onChange={handler}/>
    <button onClick={btn}>btn</button>
    <div>
      <ol>
        {
          count.map((val, ind) =>{
            return(
              <>
                <li style={{margin: '10px'}}>
                {val} <span style={pd} onClick={() =>dltcl(ind)}>Dlt</span> <span style={pd}onClick={() =>editList(ind)}>Edt</span>
                </li>
              </>
            )
          })
        }
      </ol>
    </div>
    </>
  )
}
