import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { add, crt } from './reducer'

function Counter() {
  const count = useSelector(state => state.counterq.val);
  const dispatch = useDispatch();
  const [data, setdata] = useState({
    name: ''
  });

  const handler = (e) =>{
    const {name, value} = e.target;
    setdata((oldval) =>{
      return{
        ...oldval,
        [name]: value
      }
    })
  }

  const submit = () =>{
    if(data.name.trim() !== ''){
    dispatch(add(data.name));
    data.name = "";
  }else{
    alert('Please enter name');
  }
  }

  const edit = (e) =>{
    e.preventDefault();
    dispatch(crt(3));
    //console.log(grtid);
  }

  return (
    <div>
      <div>
        <input type='text' name='name' value={data.name} onChange={handler}/>
        <button onClick={submit}>Add</button>
        <ul className='test_list'>
          {
            count.map((val, index) =>{
              return(
                  <li key={val.id}>
                  {val.value}
                  <span className='test_btn' onClick={() => edit(val.id)}>Edit</span>
                  </li>
              )
            })
          }
        </ul>
      </div>
    </div>
  )
}

export default Counter;
