import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { fetchQuotes } from './counterSlice';

export function List() {
  const dispatch = useDispatch();

  const quotes = useSelector((state) => state.val.vl);
  const status = useSelector((state) => state.val.status);
  const error = useSelector((state) => state.val.error);

  useEffect(() => {
    dispatch(fetchQuotes());
  }, [dispatch]);

  console.log(quotes);

  return (
    <div>
      <h2>Quotes</h2>

      {status === 'loading' && <p>Loading...</p>}
      {status === 'failed' && <p>Error: {error}</p>}

      {status === 'succeeded' && (
        <ul style={{listStyle:'none'}}>
          {quotes.map((q) => (
            <li key={q.id}>
              <strong>{q.id}</strong> "{q.quote}" â€” <strong>{q.author}</strong>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
