import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addTodo } from "./counterSlice";

export function Counter() {
  const todos = useSelector((state) => state.counter.todos);  // Access the list of todos
  const dispatch = useDispatch();

  const handleChange = (e) => {
    setTodo(e.target.value);
  }

  const handleSave = () => {
    dispatch(addTodo(todo));  // Dispatch the addTodo action with the new todo item
    setTodo('');  // Clear the input after adding the todo
  }

  return (
    <>
      <div className="box">
        <div className="main">
          <input
            type="text"
            name="todo"
            id="todo"
            value={todo}
            onChange={handleChange}
          />
          <button id="btn" onClick={handleSave}>Save</button>
        </div>
        <div className="div">
          <ul className="ul">
            {todos.map((item, index) => (
              <li key={index}>{item}</li>  // Display each todo item
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}
