import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { addList, dtList } from './Reducer'

const TaskManager = () => {
  const list = useSelector((state) => state.counter.value)
  const dispatch = useDispatch();

  const [inval, upinval] = useState({
    id: Date.now(),
    name: ''
  });

  const handler = (e) =>{
    const {name, value} = e.target;
    upinval((v) =>{
      return{
        ...v,
        [name] : value
      }
    });
  }

  const submit = (e) =>{
    e.preventDefault();
    dispatch(addList({ name: inval.name, id: inval.id }));
    upinval({id: Date.now(), name: '',});
  }

  const dlt = (id) =>{
    dispatch(dtList(id)); 
  }

  //console.log(list);

  return (
    <>
      <div className='main'>
        <div className='textbox'>
        <form action="" method='post'>
            <div className='allfm'>
                <div className="mb-3 mt-3">
                <input type="text" class="form-control" placeholder="Enter name" value={inval.name} name="name" onChange={handler} />
                </div>
                <button type="submit" class="btn btn-primary" onClick={submit}>Submit</button>
            </div>
        </form>
        </div>

        <div className='taskManager'>
            <div className='todo'>
                <h5>To Do</h5>
                <ol>
                {
                  list.map((v, i) =>{
                    return(
                      <>
                        <li key={v.id}>
                        {i + 1}. {v.name}
                        <i class='fas fa-trash-alt' onClick={() =>dlt(v.id)}></i>
                        </li>
                      </>
                    )
                  })
                }
                </ol>
            </div>

            <div className='doing'>
                Doing
            </div>

            <div className='done'>
                Done
            </div>
      </div>
      </div>
    </>
  )
}

export default TaskManager;
