import React from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { addData } from './counterSlice';

import { useState } from 'react';

export function List() {
  const count = useSelector((state) => state.val.vl)
  const dispatch = useDispatch()

  console.log(count);



  const [fields, setFields] = useState(['']); // start with one empty field

  // Add new empty field
  const addField = () => {
    setFields([...fields, '']);
  };

  // Update value for a specific field
  const handleChange = (index, event) => {
    const newFields = [...fields];
    newFields[index] = event.target.value;
    setFields(newFields);
  };

  return (
    <div>
      <div>
        {/* <button
          aria-label="Increment value"
          onClick={() => dispatch(increment())}
        >
          Increment
        </button>
        <span>{count}</span>
        <button
          aria-label="Decrement value"
          onClick={() => dispatch(decrement())}
        >
          Decrement
        </button>
        <button
          aria-label="Decrement value"
          onClick={() => dispatch(incrementByAmount(5))}
        >
          incrementByAmount
        </button> */}

        <button onClick={() => dispatch(addData('mango'))}>
          add
        </button>
      </div>


      <div>
      <h3>Dynamically Added Text Fields</h3>
      {fields.map((value, index) => (
        <input
          key={index}
          type="text"
          value={value}
          onChange={(e) => handleChange(index, e)}
          placeholder={`Field ${index + 1}`}
          style={{ display: 'block', marginBottom: '10px' }}
        />
      ))}

      <button onClick={addField}>Add Field</button>
    </div>
    </div>
  )
}
