import { useDrag, useDrop } from "react-dnd";
import { DndProvider } from "react-dnd";
import { HTML5Backend } from "react-dnd-html5-backend";

const ItemType = "BOX";

const DraggableBox = ({ name }) => {
  const [{ isDragging }, drag] = useDrag(() => ({
    type: ItemType,
    item: { name },
    collect: (monitor) => ({
      isDragging: !!monitor.isDragging(),
    }),
  }));

  return (
    <div
      ref={drag}
      style={{
        padding: "10px",
        background: "lightblue",
        marginBottom: "8px",
        opacity: isDragging ? 0.5 : 1,
      }}
    >
      {name}
    </div>
  );
};

const DropZone = ({ onDrop }) => {
  const [, drop] = useDrop(() => ({
    accept: ItemType,
    drop: (item) => onDrop(item),
  }));

  return (
    <div ref={drop} style={{ padding: "20px", border: "2px dashed black" }}>
      Drop Here
    </div>
  );
};

const DragDropDnd = () => {
  return (
    <DndProvider backend={HTML5Backend}>
      <DraggableBox name="Drag Me" />
      <DropZone onDrop={(item) => alert(`Dropped: ${item.name}`)} />
    </DndProvider>
  );
};

export default DragDropDnd;



