import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  value: 0,
  arr: []
}

export const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    increment: (state) => {
      state.value += 1
    },
    decrement: (state) => {
      state.value -= 1
    },
    incrementByAmount: (state, action) => {
      state.value += action.payload
    },
    addList: (state, action) =>{
     state.arr.push(action.payload);
    },
    deleteList: (state, action) =>{
      state.arr = state.arr.filter((gi) => gi.id !== action.payload);
    },
    editList: (state, action) =>{
      const {id, pname} = action.payload;
      const index = state.arr.findIndex((i) => i.id === id);
      if (index !== -1){
        state.arr[index].pname = pname;
      }
    }
  },
})

// Action creators are generated for each case reducer function
export const { addList, deleteList, editList } = counterSlice.actions

export default counterSlice.reducer
