import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { increment } from './reducer'

function Counter() {
  const count = useSelector(state => state.counterq.val);
  const dispatch = useDispatch();
  const [fvalue, setfvalue] = useState(0);
  const [data, setdata] = useState({
    name: ''
  });

  const handler = (e) =>{
    const {name, value} = e.target;
    setdata((oldval) =>{
      return{
        ...oldval,
        [name]: value
      }
    })
  }

  const submit = () =>{
    setfvalue(data.name);
    dispatch(increment(fvalue));
  }

  console.log(fvalue);

  return (
    <div>
      <div>
        <input type='text' name='name' onChange={handler}/>
        {/* <button onClick={() => dispatch(increment(5))}>Add</button> */}
        <button onClick={submit}>Add</button>
        <span>{count}</span>
      </div>
    </div>
  )
}

export default Counter;
