import React, { useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { addTodo, deleteTodo } from "./counterSlice";

export function Counter() {
  const todos = useSelector((state) => state.counter.todos);  // Access the list of todos
  const dispatch = useDispatch();
  const [todo, setTodo] = useState('');

  const handleChange = (e) => {
    setTodo(e.target.value);
  };

  const handleSave = () => {
    const newTodo = {
      id: Date.now(), // Generate a unique id using the current timestamp
      text: todo
    };
    dispatch(addTodo(newTodo));  // Dispatch the addTodo action with the new todo item
    setTodo('');  // Clear the input after adding the todo
  };

  const handleDelete = (id) => {
    dispatch(deleteTodo(id));  // Dispatch the deleteTodo action with the todo id
  };

  return (
    <>
      <div className="box">
        <div className="main">
          <input type="text" name="todo" id="todo" value={todo} onChange={handleChange} />
          <button id="btn" onClick={handleSave}>Save</button>
        </div>
        <div className="div">
          <ul className="ul">
            {todos.map((item) => (
              <li key={item.id}>
                {item.text}
                <span onClick={() => handleDelete(item.id)} style={{cursor:'pointer'}}>x</span>
                <span onClick={() => handleDelete(item.id)} style={{cursor:'pointer'}}>Edit</span>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}
