import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { list, deleteItem, editItem } from './counterSlice';

export function Counter() {
  const [val, setVal] = useState('');
  const [editId, setEditId] = useState(null);
  const [editValue, setEditValue] = useState('');
  const count = useSelector((state) => state.counter.val);
  const dispatch = useDispatch();

  const handleAdd = () => {
    if (val.trim()) {
      dispatch(list(val));
      setVal('');
    }
  };

  const handleEdit = (id, value) => {
    setEditId(id);
    setEditValue(value);
  };

  const handleEditSubmit = () => {
    dispatch(editItem({ id: editId, newvl: editValue }));
    setEditId(null);
    setEditValue('');
  };

  return (
    <>
      <input
        type="text"
        value={val}
        onChange={(e) => setVal(e.target.value)}
        placeholder="Add item"
      />
      <button onClick={handleAdd}>Add</button>
      <div>
        <ol>
          {count.map((item) => (
            <li key={item.id} style={{ margin: '10px' }}>
              {editId === item.id ? (
                <>
                  <input
                    type="text"
                    value={editValue}
                    onChange={(e) => setEditValue(e.target.value)}
                  />
                  <button onClick={handleEditSubmit}>Save</button>
                </>
              ) : (
                <>
                  {item.value}{' '}
                  <span
                    style={{ padding: '2px', backgroundColor: '#ccc', cursor: 'pointer' }}
                    onClick={() => dispatch(deleteItem(item.id))}
                  >
                    Delete
                  </span>{' '}
                  <span
                    style={{ padding: '2px', backgroundColor: '#ccc', cursor: 'pointer' }}
                    onClick={() => handleEdit(item.id, item.value)}
                  >
                    Edit
                  </span>
                </>
              )}
            </li>
          ))}
        </ol>
      </div>
    </>
  );
}
