import { createSlice } from '@reduxjs/toolkit'

const initialState = {
  todos: [],  // Store a list of todos
}

export const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    addTodo: (state, action) => {
      state.todos.push(action.payload);  // Add the new todo item to the list
    },
    editTodo: (state, action) => {
      const { id, newText } = action.payload;
      const todo = state.todos.find(todo => todo.id === id);
      if (todo) {
        todo.text = newText;  // Update the text of the found todo item
      }
    },
    deleteTodo: (state, action) => {
      state.todos = state.todos.filter(todo => todo.id !== action.payload);
    },
  },
})

// Action creators are generated for each case reducer function
export const { addTodo, deleteTodo, editTodo } = counterSlice.actions
export default counterSlice.reducer
