import { createSlice } from '@reduxjs/toolkit'

const initialState = {
 val: [],
}

export const counterSlice = createSlice({
  name: 'counter',
  initialState,
  reducers: {
    list: (state, action) =>{
      state.val.push(action.payload);
    },
    deleteItem: (state, action) => {
      state.val = state.val.filter((_, i) => i !== action.payload);
    },
    editItem: (state, action) =>{
      const { id, newvl } = action.payload;
      const index = state.val.findIndex((item) => item.id === id); // Find index by id
      console.log(index);
      if (index !== -1) {
        state.val[index].value = newvl; // Update the item's value
      }
    }
  },
})

// Action creators are generated for each case reducer function
export const {list, deleteItem, editItem} = counterSlice.actions

export default counterSlice.reducer
