import { useEffect } from 'react';
import io from 'socket.io-client';

const Chat = () => {
  useEffect(() => {
    const socket = io(); // Initialize socket connection
    socket.on('connect', () => {
      console.log('Connected to socket server');
    });

    // Cleanup on component unmount
    return () => socket.disconnect();
  }, []);

  return (
    <>
      <h4>Chatting</h4>
    </>
  );
};

export default Chat;
