import { useState } from 'react';
import QuizData from './QuizData';

const Faq = () =>{
    const [num, numData] = useState(0);
    const vl = QuizData[num];
    const lt = QuizData.length-1;
    let sr = 1;

    const next = () =>{
        numData(num + 1);
        if(num === lt){
            numData(0);
        }
    }

    console.log(num);

    return(
        <>
            <div className="quiz">
                <h4>{sr}. {vl.qs}</h4>

                <div className="rbtn">
                <input type="radio" name="ans"/><label>{vl.a}</label>
                </div>
                <div className="rbtn">
                <input type="radio" name="ans"/><label>{vl.b}</label>
                </div>
                <div className="rbtn">
                <input type="radio" name="ans"/><label>{vl.c}</label>
                </div>
                <div className="rbtn">
                <input type="radio" name="ans"/><label>{vl.d}</label>
                </div>
                <button type="button" onClick={next}>Next</button>
            </div>
        </>
    )
}

export default Faq;
