import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { addList } from './Reducer'

const TaskManager = () => {
  const count = useSelector((state) => state.counter.value)
  const dispatch = useDispatch();

  const [inval, upinval] = useState({
    name : ''
  });

  const handler = (e) =>{
    const {name, value} = e.target;
    upinval((v) =>{
      return{
        ...v,
        [name] : value
      }
    });
  }

  const submit = (e) =>{
    e.preventDefault();
    dispatch(addList(inval.name));
    upinval({name: ''})
  }

  console.log(count);

  return (
    <>
      <div className='main'>
        <div className='textbox'>
        <form action="" method='post'>
            <div className='allfm'>
                <div className="mb-3 mt-3">
                <input type="text" class="form-control" placeholder="Enter name" value={inval.name} name="name" onChange={handler} />
                </div>
                <button type="submit" class="btn btn-primary" onClick={submit}>Submit</button>
            </div>
        </form>
        </div>

        <div className='taskManager'>
            <div className='todo'>
                To Do
            </div>

            <div className='doing'>
                Doing
            </div>

            <div className='done'>
                Done
            </div>
      </div>
      </div>
    </>
  )
}

export default TaskManager;
