import React, { useEffect, useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import {list} from './counterSlice'

export function Counter() {
  const count = useSelector((state) => state.counter.val)
  const dispatch = useDispatch();
  const [dt, dtSet] = useState({
    name : ''
  });
  const [vl, setval] = useState();

  const handler = (e) =>{
    const {name, value} = e.target;
    dtSet((pv) =>{
      return {...pv,
        [name]: value
      }
    });
  }

  const btn = () =>{
    setval(dt.name);
    dt.name = '';
  }

  console.log(vl);

  //console.log(count);
  return (
    <>
    <span>{count}</span>
    <div>
      <input type='text' name='name' value={dt.name} onChange={handler} />
      <button onClick={btn}>button</button>
    </div>
    </>
  )
}
