import React, { useState } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { decrement, increment, addList } from './counterSlice'

const Etracker = () => {
  const count = useSelector((state) => state.counter.value);
  const ar = useSelector((state) => state.counter.arr);
  const dispatch = useDispatch();
  const [pname, setPname] = useState(null);
  const [amt, setAmt] = useState(null);

  const [details, setDetails] = useState({
    pname : '',
    amount : ''
  });

  const Handler = (e) =>{
    const {name, value} = e.target;
    setDetails((vl) =>{
      return {
        ...vl,
        [name] : value
      }
    });
  }

  const submit = () =>{
    setPname(details.pname);
    setAmt(details.amount);
    dispatch(addList({pname: details.pname, amt: details.amount}));
  }

  //console.log(details);
  console.log(ar);

  return (
    <>
    <p>{pname}{amt}</p>
      {/* <div>
        <button
          aria-label="Increment value"
          onClick={() => dispatch(increment())}
        >
          Increment
        </button>
        <span>{count}</span>
        <button
          aria-label="Decrement value"
          onClick={() => dispatch(decrement())}
        >
          Decrement
        </button>
      </div> */}

      <div className='etracker_main'>
        <div className='your_balance'>
          <p>Your Balance</p>
          <h4>Rs. 1500</h4>
        </div>

        <div className='income_expense'>
          <div className='income_expense_left'>
            <h4>Income</h4>
            <p>Rs.600</p>
          </div>
          <div className='income_expense_right'>
            <h4>Expense</h4>
            <p>Rs.600</p>
          </div>
        </div>

        <div className='etrack_exp_list'>
          <p>Expence List</p>
          <ul>
              <li>name</li>
              <li>amount</li>
          </ul>
        </div>

        <div className='add_new_transaction'>
          <form>
            <p>Product Name</p>
            <input type='text' name='pname' onChange={Handler} />
            <p>Amount</p>
            <input type='number' name='amount' onChange={Handler} />
            <div className='etrack_btn'><button type='button' name='btn' onClick={submit}>Add</button></div>
          </form>
        </div>
      </div>
    </>
  )
}

export default Etracker;
