import {useEffect, useState} from 'react';
import {fetchApiData} from './reducer'
import { useSelector, useDispatch } from 'react-redux';

const Api = () =>{
    const count = useSelector(state => state.counterq.val);
    const apiData = useSelector((state) => state.counterq.apiData);
    const status = useSelector((state) => state.counterq.status);
    const error = useSelector((state) => state.counterq.error);
    const dispatch = useDispatch();
    const [num, setNum] = useState();

    const handler = (e) =>{
        setNum(e.target.value);
    }

    useEffect(() =>{
        dispatch(fetchApiData(num));
    }, [num]);

    //console.log(apiData);

    return(
        <>
            {count}
            <select className="form-select" style={{width:'200px'}} onChange={handler}>
                <option value=''>select</option>
                <option value='1'>1</option>
                <option value='2'>2</option>
                <option value='3'>3</option>
                <option value='4'>4</option>
                <option value='5'>5</option>
            </select>

        <div style={{ marginTop: '20px' }}>
        {status === 'loading' && <p>Loading...</p>}
        {status === 'failed' && <p>Error: {error}</p>}
        {apiData && (
          <div>
            <h3>{apiData.title}</h3>
            <p>{apiData.body}</p>
        </div>
        )}
      </div>
        </>
    )
}

export default Api;
