export const fetchApiData = createAsyncThunk(
  'counter/fetchApiData',
  async (id, { rejectWithValue }) => {
    try {
      const response = await axios.get(`https://jsonplaceholder.typicode.com/posts/${id}`);
      return response.data;
    } catch (error) {
      return rejectWithValue(error.response.data);
    }
  }
);

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    val: [],
    apiResult: null,
    status: 'idle', // "idle" | "loading" | "succeeded" | "failed"
    error: null
  },
  reducers: {
    add: (state, actions) => {
      state.val.push({ id: nanoid(), value: actions.payload });
    },
    edt: (state, actions) => {
      const { id, value } = actions.payload;
      const ind = state.val.findIndex((d) => d.id === id);
      const pr = prompt('Enter Value', value);
      if (pr !== null) state.val[ind].value = pr;
    },
    dlt: (state, actions) => {
      state.val = state.val.filter((v) => v.id !== actions.payload);
    },
    apiData: (state, actions) => {
      state.apiResult = actions.payload;
    }
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchApiData.pending, (state) => {
        state.status = 'loading';
      })
      .addCase(fetchApiData.fulfilled, (state, action) => {
        state.status = 'succeeded';
        state.apiResult = action.payload;
      })
      .addCase(fetchApiData.rejected, (state, action) => {
        state.status = 'failed';
        state.error = action.payload;
      });
  }
});

export const { add, edt, dlt, apiData } = counterSlice.actions;
export default counterSlice.reducer;
