import { createSlice } from '@reduxjs/toolkit';
import { nanoid } from 'nanoid';

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    val: []
  },
  reducers: {
    add: (state, actions) => {
      //state.val.push(actions.payload);
      state.val.push({id: nanoid(), value: actions.payload});
    },
    edt: (state, actions) =>{
      //console.log(actions.payload.id);
      //console.log(actions.payload.value);
      const item = state.payload.find(item => item.id === action.payload.id);
      if (item) {
        item.value = action.payload.value;
      }
    },
    dlt: (state, actions) =>{
      state.val = state.val.filter((v) =>{
        return v.id !== actions.payload;
      })
    }
  }
});

// Action creators are generated for each case reducer function
export const { add, edt, dlt} = counterSlice.actions

export default counterSlice.reducer
